<?phpnamespace App;require_once (__DIR__ . "/../DatabaseConnector.php");function showLadder(){    $show = false;    $dbc = Connect();    $sql = "SELECT * FROM `tbl_matches` WHERE played = 'F'";    $gamesNotPlayed = $dbc->query($sql)->rowCount();    if ($gamesNotPlayed == 0)    {        $show = true;    }    return $show;}function fillLadder(){    $dbc = Connect();    $sql = "SELECT `name`,`points` FROM `tbl_teams` WHERE `poule_id` = 1 ORDER BY `points` LIMIT 2";    $pool1 = $dbc->query($sql)->fetchAll(\PDO::FETCH_ASSOC);    $sql = "SELECT `name`,`points` FROM `tbl_teams` WHERE `poule_id` = 2 ORDER BY `points` LIMIT 2";    $pool2 = $dbc->query($sql)->fetchAll(\PDO::FETCH_ASSOC);    $sql = "SELECT `name`,`points` FROM `tbl_teams` WHERE `poule_id` = 3 ORDER BY `points` LIMIT 2";    $pool3 = $dbc->query($sql)->fetchAll(\PDO::FETCH_ASSOC);    $sql = "SELECT `name`,`points` FROM `tbl_teams` WHERE `poule_id` = 4 ORDER BY `points` LIMIT 2";    $pool4 = $dbc->query($sql)->fetchAll(\PDO::FETCH_ASSOC);    if ($pool1 != null && $pool2 != null && $pool3 != null && $pool4 != null)    {        $pool = array_merge($pool1, $pool2, $pool3, $pool4);        foreach ($pool as $key => $row)        {            $points[$key] = $row['points'];        }        array_multisort($points, SORT_DESC, $pool);        /*         * 1 tegen nummer 8         *         * 2 tegen nummer 7         *         * 3 tegen nummer 6         *         * 4 tegen nummer 5         * */        $games = array        (            "game1" => array("team1" => $pool[0]['name'], "team2" => $pool[7]['name']),            "game2" => array("team1" => $pool[1]['name'], "team2" => $pool[6]['name']),            "game3" => array("team1" => $pool[2]['name'], "team2" => $pool[5]['name']),            "game4" => array("team1" => $pool[3]['name'], "team2" => $pool[4]['name'])        );        return $games;    }    return 0;}